/*  Purpose: To manage and display one NFT Card  */

import React from 'react';
import './NFTCard.css';

function NFTCard({ card }) {
    return (
        <div className="card">
            <img src={card.image} alt={card.name} className="card-image" />
            <h3>{card.name}</h3>
            <p>Created by: {card.description}</p>
            <a href={card.url} target="_blank" rel="noopener noreferrer">View on OpenSea</a>
        </div>
    );
}

export default NFTCard;
