// src/components/MainInput.js
import './MainInput.css';
import React, { useState } from 'react';

function MainInput() {
    // State to store the list of entered NFT URLs.
    const [nftUrls, setNftUrls] = useState([]);

    // State to store the current value of the input box.
    const [currentInput, setCurrentInput] = useState('');

    // 1 for input step, 2 for details step
    const [currentStep, setCurrentStep] = useState(1);

    // Input char count listeners
    const [displayNameCount, setDisplayNameCount] = useState(0);
    const [descriptionCount, setDescriptionCount] = useState(0);

    return (
        <div>
            {currentStep === 1 ? (
                <>
                    <div className="input-container">
                        <input
                            type="text"
                            placeholder="Enter NFT URL..."
                            value={currentInput}
                            onChange={(e) => setCurrentInput(e.target.value)}
                        />
                        <button onClick={() => {
                            if (nftUrls.length < 8 && currentInput && !nftUrls.includes(currentInput)) {
                                setNftUrls([...nftUrls, currentInput]);
                                setCurrentInput('');
                                if (nftUrls.length === 7) {
                                    setCurrentStep(2);
                                }
                            } else {
                                alert("URL already added or input is empty!");
                            }
                        }}>
                            Add NFT
                        </button>
                    </div>
                    <div className="counter">
                        {nftUrls.length} / 8
                    </div>
                    <div className="url-list">
                        {nftUrls.map((url, index) => (
                            <div key={index} className="url-card">
                                {url}
                            </div>
                        ))}
                    </div>
                    <button onClick={() => setCurrentStep(2)}>Next Step</button>
                </>
            ) : (
                <>
                    {/* All the details related elements */}
                    <input
                        placeholder="Display Name"
                        maxLength={69}
                        onChange={(e) => setDisplayNameCount(e.target.value.length)}
                    />
                    <textarea
                        placeholder="Description (max 420 characters)"
                        maxLength={420}
                        onChange={(e) => setDescriptionCount(e.target.value.length)}
                    ></textarea>

                    {/* ... add other details elements here ... */}
                </>
            )}
        </div>
    );


}

export default MainInput;
