import React, { useState } from 'react';
//new
import axios from "axios";
import './MainInput.css';

function MainInput() {
    const [currentInput, setCurrentInput] = useState('');
    const [nftUrls, setNftUrls] = useState([]);
    const [currentStep, setCurrentStep] = useState(1);
    const [displayNameCount, setDisplayNameCount] = useState(0);
    const [descriptionCount, setDescriptionCount] = useState(0);

    //new
    const fetchNftMetadata = async (url) => {
        const response = await axios.get(url);
        const metadata = response.data;

        return metadata;
    };

    //new
    const loadNFT = async (url) => {
        const metadata = await fetchNftMetadata(url);

        setNftMetadata([...nftMetadata, metadata]);
    };

    //new
    const addNftToCard = async () => {
        await loadNFT(currentInput);
        setCurrentInput("");
    };

    return (
        <div>
            {currentStep === 1 && (
                <>
                    <div className="input-container">
                        <input 
                            type="text" 
                            placeholder="Enter NFT URL..." 
                            value={currentInput} 
                            onChange={(e) => setCurrentInput(e.target.value)} 
                        />

                        {/* You are here */}
                        <button onClick={() => {
                            if (nftUrls.length < 8 && currentInput && !nftUrls.includes(currentInput)) {
                                setNftUrls([...nftUrls, currentInput]);
                                setCurrentInput('');
                            } else {
                                alert("URL already added or input is empty!");
                            }
                        }}>
                            Add NFT
                        </button>
                    </div>
                    <div className="counter">
                        {nftUrls.length} / 8
                    </div>
                    <div className="url-list">
                        {nftUrls.map((url, index) => (
                            <div key={index} className="url-card">
                                {url}
                            </div>
                        ))}
                    </div>
                    <button onClick={() => setCurrentStep(2)}>Next Step</button>
                </>
            )}

            {currentStep === 2 && (
                <>
                    <div className="input-counter">
                        <input 
                            placeholder="Display Name"
                            maxLength={69} 
                            onChange={(e) => {
                                setDisplayNameCount(e.target.value.length);
                            }}
                        />
                        <span>{displayNameCount} / 69</span>
                    </div>

                    <div className="textarea-counter">
                        <textarea 
                            placeholder="Description (max 420 characters)" 
                            maxLength={420} 
                            onChange={(e) => {
                                setDescriptionCount(e.target.value.length);
                            }}
                        ></textarea>
                        <span>{descriptionCount} / 420</span>
                    </div>

                    {/* ... other details step elements ... */}

                    <button onClick={() => setCurrentStep(1)}>Previous</button>
                </>
            )}
        </div>
    );
}

export default MainInput;
